function readFile(a)local b=fs.open(a,"r")if not b then error("Could not open file: "..a)end;local c=b.readAll()b.close()return c end;function deleteDirectory(a)if not fs.exists(a)then return end;if not fs.isDir(a)then fs.delete(a)return end;for d,e in ipairs(fs.list(a))do deleteDirectory(fs.combine(a,e))end;fs.delete(a)end;print("Starting...")info=textutils.unserializeJSON(readFile("info.json"))startup=info.startup;if not startup then shell.run("run.lua")end;if startup.clearLogs then print(" | Clearing old logs")deleteDirectory("app/logs")end;if startup.delaySeconds then print(" | Waiting for "..startup.delaySeconds.." seconds...")os.sleep(startup.delaySeconds)end;if startup.autoUpdate then shell.run("update.lua")end;shell.run("run.lua")