baseUrl="https://raw.githubusercontent.com/7obyGit/C-183501-124812851/main/cc2/src/entrypoints/tests/release"function download(a)local b=http.get(a)local c=b.readAll()c=c:sub(1,1)=="?"and c:sub(2)or c;b.close()return c end;function writeFile(d,e)local f=fs.getDir(d)if f~=""then createDirectory(f)end;local g=fs.open(d,"w")g.write(e)g.close()end;function readFile(d)local g=fs.open(d,"r")if not g then error("Could not open file: "..d)end;local e=g.readAll()g.close()return e end;local h=download(baseUrl.."/update.lua")writeFile("/update.lua",h)shell.run("update.lua")info=textutils.unserializeJSON(readFile("info.json"))print("\nDeployment completed successfully")print(" - v"..info.version)print(" - Installed "..#info.deployment.manifest.." files")local i=info.deployment.completeMessage;if i then print("\n"..i)end;os.sleep(3)local j=info.deployment.completeScript;if j then print("\nComplete Script Detected")print(" | Retrieved `deployment.completeScript`")for k,l in ipairs(j)do print(" | > "..l)shell.run(l)end;print(" | Complete!")end